mapper.select:
select *
from solutionComment
where SLTID = #{SLTID}
limit #{Begin} ,#{Limit};

select count(*)
from solutioncomment
where SLTID = #{SLTID};

mapper.update:
update solution
set CommentCount = CommentCount + #{Interval}
where SLTID = #{SLTID};

mapper.delete:
delete
from solutioncomment
where SLTCMTID = #{SLTCMTID}
  and UID = #{UID};
